import React , {useRef, useState} from 'react'
import Input from '../../UI/Input';

import classes from './MealItemForm.module.css'

const MealItemForm = (props) => {

  const[amountIsValid, setAmountIsValid] = useState(true);

  const amountInputRef = useRef();

  const submitHandler = event => {
    event.preventDefault(); //so that form doesn't submit itslf.

    const eneteredAmount = amountInputRef.current.value;
    const eneteredAmountNumber = +eneteredAmount;

    if(
      eneteredAmount.trim().length === 0 || 
      eneteredAmountNumber < 1 ||
      eneteredAmountNumber > 50
    ){
      setAmountIsValid(false);
      return;
    }

    props.onAddToCart

  };


  return (
    <form className={classes.form} onSubmit = {submitHandler}>
        <Input
        ref = {amountInputRef}
        label = "Amount" 
        input = {{
            id: 'amount' + props.id,
            type: 'number',
            min: '1',
            max: '50',
            step: '1',
            defaultValue: '1'
        }}/>
        <button> + Add</button>
        {!amountIsValid && <p>Please enter a valid amount (1-50).</p>}
    </form>
  )
};

export default MealItemForm;
